const minRaw =  $json["query"]["min"];
const maxRaw =  $json["query"]["max"];

// Verifica se os valores existem
if (minRaw === undefined || maxRaw === undefined) {
  throw new Error("Parâmetros 'min' e 'max' são obrigatórios.");
}

// Verifica se são apenas numeros inteiros (sem letras, espaços, etc.)
const isNumeric = (value) => /^-?\d+$/.test(value);

if (!isNumeric(minRaw) || !isNumeric(maxRaw)) {
  throw new Error("Dados inválidos. Somente números inteiros são permitidos.");
}

const min = parseInt(minRaw);
const max = parseInt(maxRaw);

// Verifica se min é menor que max
if (min >= max) {
  throw new Error("'min' deve ser menor que 'max'.");
}

// Gera número aleatório
const random = Math.floor(Math.random() * (max - min + 1)) + min;

return {
  json: {
    numeroAleatorio: random
  }
};
